mapper.select:

SELECT *
FROM	(SELECT rownum r, a.*

		FROM 	(SELECT ad_no adNo, ad_div adDiv, substr(user_id, 1, 4) || '****' userId, school_cate schoolCate, school_code schoolCode, content, upvote, downvote, regit_date regitDate
				FROM eval_school
				WHERE school_cate = #{schoolCate}
				AND school_code = #{schoolCode}
				AND ad_div = #{adDiv}
				AND state = 'a'
				
					ORDER BY upvote/(downvote+1) DESC) a
				
				
					ORDER BY upvote DESC) a
				
				
					ORDER BY ad_no DESC) a
				

		WHERE rownum <= #{endRow}) 
WHERE r >= #{startRow}

SELECT count(*)
FROM eval_school
WHERE school_cate = #{schoolCate}
AND school_code = #{schoolCode}
AND ad_div = #{adDiv}
AND state = 'a'

SELECT updown
FROM eval_ud_log
WHERE ad_no = #{adNo}
AND user_id = #{userId}

mapper.update:
UPDATE eval_school
SET 

	upvote = upvote + 1


	downvote = downvote + 1

WHERE ad_no = #{adNo}
AND user_id = #{userId}

UPDATE eval_school
SET 

	upvote = upvote - 1


	downvote = downvote - 1

WHERE ad_no = #{adNo}
AND user_id = #{userId}

mapper.delete:
  		DELETE eval_ud_log 
		WHERE ad_no = #{adNo}
		AND user_id = #{userId}

mapper.select:
SELECT upvote, downvote
FROM eval_school
WHERE ad_no = #{adNo}
